use ansi_colours::ansi256_from_rgb;
use ansi_colours::rgb_from_ansi256;

fn main() {
    // println!("Hello, world!");
    // let colors = vec!["255;74;68", "122;112;112", "102;217;239"];
    // for c in colors {
    //     let sp: Vec<&str> = c.split(";").collect();
    //     let r = sp[0].parse::<u8>().unwrap();
    //     let g = sp[1].parse::<u8>().unwrap();
    //     let b = sp[2].parse::<u8>().unwrap();
    //     let co = ansi256_from_rgb([r, g, b]);
    //     let rgb = rgb_from_ansi256(co);
    //     println!("c: {} co: {} rgb: {},{},{}", c, co, rgb.0, rgb.1, rgb.2);
    // }

    let colors = get_lscolors_str();
    let mut output_colors = vec![];
    for color in colors {
        let tokens: Vec<&str> = color.split(";").collect();
        match tokens.len() {
            11 => {
                // "or=0;38;2;0;0;0;48;2;255;74;68"
                let t1: Vec<&str> = tokens[0].split("=").collect();
                let ext = t1[0];
                let fg_r = tokens[3].parse::<u8>().unwrap();
                let fg_g = tokens[4].parse::<u8>().unwrap();
                let fg_b = tokens[5].parse::<u8>().unwrap();
                let bg_r = tokens[8].parse::<u8>().unwrap();
                let bg_g = tokens[9].parse::<u8>().unwrap();
                let bg_b = tokens[10].parse::<u8>().unwrap();
                let fg_idx = ansi256_from_rgb([fg_r, fg_g, fg_b]);
                let bg_idx = ansi256_from_rgb([bg_r, bg_g, bg_b]);
                let fg_rgb = rgb_from_ansi256(fg_idx);
                let bg_rgb = rgb_from_ansi256(bg_idx);
                let converted = format!(
                    "{}=0;38;2;{};{};{};48;2;{};{};{}",
                    ext, fg_rgb.0, fg_rgb.1, fg_rgb.2, bg_rgb.0, bg_rgb.1, bg_rgb.2
                );
                output_colors.push(converted);
            }
            6 => {
                // "ln=0;38;2;249;38;114",
                let t1: Vec<&str> = tokens[0].split("=").collect();
                let ext = t1[0];
                let fg_r = tokens[3].parse::<u8>().unwrap();
                let fg_g = tokens[4].parse::<u8>().unwrap();
                let fg_b = tokens[5].parse::<u8>().unwrap();
                let fg_idx = ansi256_from_rgb([fg_r, fg_g, fg_b]);
                let fg_rgb = rgb_from_ansi256(fg_idx);
                let converted = format!("{}=0;38;2;{};{};{}", ext, fg_rgb.0, fg_rgb.1, fg_rgb.2);
                output_colors.push(converted);
            }
            _ => output_colors.push(color.to_string()),
        }
    }

    let ls_colors_256 = output_colors.join(":");
    println!("{}", ls_colors_256);
}

